import React from 'react';
import Info from './componets/Info';
import Rate from './componets/Rate';
import Form from './componets/Form';

const API_KEY = "9ef427ff057a6ea0fa8c51701d1fecdc";

class App extends React.Component {

  state = {
    temp: undefined,
    city: undefined,
    country: undefined,
    sunrise: undefined,
    sunset: undefined,
    error: undefined
  }

  gettingWeather = async (e) => {
    e.preventDefault();
    const city = e.target.elements.city.value;

    if(city) { // Проверка введены ли данные
      const api_url = 
      await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}`);
      const data = await api_url.json(); // Преобразование в json

      var sunrise = data.sys.sunrise;
      var date_rise = new Date();
      date_rise.setTime(sunrise);
      var sunrise_date = date_rise.getHours() + ":" + date_rise.getMinutes() + ":" + date.getSeconds();

      var sunset = data.sys.sunset;
      var date = new Date();
      date.setTime(sunset);
      var sunset_date = date.getHours() + ":" + date.getMinutes() + ":" + date.getSeconds();


      this.setState({ // Получение данных из json файла
        temp: data.main.temp,
        city: data.name,
        country: data.sys.country,
        sunrise: sunrise_date,
        sunset: sunset_date,
        error: ""
      });
    }
  }

  render() {
    return (
      <div>
        <Info /> 
        <Form weatherMethod={this.gettingWeather} />
        <Rate 
          temp={this.state.temp}
          city={this.state.city}
          country={this.state.country}
          sunrise={this.state.sunrise}
          sunset={this.state.sunset}
        />
      </div>
    );
  }
}

export default App;
